import React, { createContext, useState, useEffect } from "react";
export const ShopContext = createContext(null);

const getDefaulCart = () => {
  let cart = {};
  for (let index = 0; index < 300 + 1; index++) {
    cart[index] = 0;
  }
  return cart;
};

const ShopContextProvider = (props) => {
  const [cartItem, setcartItem] = useState(getDefaulCart());
  const [all_product,setall_product]=useState([]);

  useEffect(()=>{
    fetch('http://4000/all_product')
  },[])

  const addToCart = (itemId) => {
    setcartItem((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }));
    console.log(cartItem);
  };

  const removeFromCart = (itemId) => {
    setcartItem((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }));
  };

  const getTotalCartAmount=()=>{
    let Totalamt=0;
    for(const item in cartItem){
      if(cartItem[item] > 0){
        let itemInfo=all_product.find((product)=>product.id===Number(item))
        Totalamt += cartItem[item]*(itemInfo.new_price)
      }
    }
    return Totalamt;
  }
  const getTotalCartItems=()=>{
    let totalItem=0;
    for(const item in cartItem){
      if(cartItem[item] > 0){
        let itemInfo=all_product.find((product)=>product.id===Number(item))
        totalItem += cartItem[item]
      }
    }
    return totalItem;
  }

  const contextvalue = { all_products, cartItem, addToCart, removeFromCart, getTotalCartAmount,getTotalCartItems };

  return (
    <ShopContext.Provider value={contextvalue}>
      {props.children}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
